package trial;

public class Driving {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mark=100;
		if(mark==80)
		{
			System.out.println("Passed with 80 marks");
		}
		else if(mark==90)
		{
			System.out.println("Passed with 90 marks");
		}
		else if(mark==100)
		{
			System.out.println("Passed with 100 marks");
		}
		else
		{
			System.out.println("Failed");
		}

	}

}
